package matrices;

public class matriz_inversa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Ejemplo de matriz en java:");
		System.out.println(" ");
		
		int[] matriz = {1,2,3,4,5};
		
		for (int f=4;f>=0;f--) { // la matriz empieza en 0
			
			System.out.println("Valor matriz indice: "+f+" valor: "+matriz[f]);
		}
	}

}
